/**
 * Exibe o icone svg de acordo com o status informado.
 *
 * @param int $pStatus
 * @return string
 */
if (! function_exists('load_icon_by_status')) {
    function load_icon_by_status(int $pStatus): string
    {
        if ($pStatus === 2) {
            $svg = TIMES;
        } elseif ($pStatus === 3) {
            $svg = REDIRECT;
        } else {
            $svg = HOURGLASS;
        }

        return $svg;
    }
}

/**
 * Exibe o icone svg de acordo com o grupo informado.
 *
 * @param int $pStatus
 * @return string
 */
if (! function_exists('load_icon_by_group')) {
    function load_icon_by_group(int $pGroupId)
    {
        if ($pGroupId === 1) { // Environment
            $svg = AUTUMN_LEAF;
        } elseif ($pGroupId === 2) { // Social
            $svg = PEOPLE;
        } else { // Governance
            $svg = BANK;
        }

        return $svg;
    }
}

/**
 * Redimenciona uma imagem e salva no diretorio indicado.
 *
 * @param array $pImage
 * @param integer $pWidth
 * @param string $pFolder
 * @return string
 */
if (! function_exists('resize')) {
    function resize(array $pImage, int $pWidth, string $pFolder): string
    {
        $rand = rand(0, getrandmax());
        $uniq = uniqid($rand, true);
        $name = md5($uniq);

        if ($pImage['type'] == "image/jpeg") {
            $img = imagecreatefromjpeg($pImage['tmp_name']);
        } elseif ($pImage['type'] == "image/gif") {
            $img = imagecreatefromgif($pImage['tmp_name']);
        } elseif ($pImage['type'] == "image/png") {
            $img = imagecreatefrompng($pImage['tmp_name']);
        }

        $x   = imagesx($img);
        $y   = imagesy($img);
        $altura = ($pWidth * $y) / $x;
        $newImage = imagecreatetruecolor($pWidth, $altura);
        imagecopyresampled($newImage, $img, 0, 0, 0, 0, $pWidth, $altura, $x, $y);

        if ($pImage['type'] == "image/jpeg") {
            $local = "$pFolder/$name".".jpg";
            imagejpeg($newImage, $local);
        } elseif ($pImage['type'] == "image/gif") {
            $local = "$pFolder/$name".".gif";
            imagejpeg($newImage, $local);
        } elseif ($pImage['type'] == "image/png") {
            $local = "$pFolder/$name".".png";
            imagejpeg($newImage, $local);
        }

        imagedestroy($img);
        imagedestroy($newImage);

        return $local;
    }
}
